import React from 'react';
import { useSelector } from 'react-redux';

const SummaryPage = () => {
    const formData = useSelector(state => state.formData);

    // Inline styles for the header and content
    const containerStyle = {
        maxWidth: '600px',
        margin: '0 auto',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    };

    const headerStyle = {
        marginBottom: '20px',
    };

    const titleStyle = {
        fontSize: '24px',
        marginBottom: '10px',
        textAlign: 'center',
        color: '#333',
    };

    const lineStyle = {
        border: 'none',
        borderTop: '2px solid #ccc',
        margin: '10px 0 20px',
    };

    const contentStyle = {
        fontSize: '18px',
        color: '#555',
    };

    const itemStyle = {
        marginBottom: '10px',
    };

    const boldTextStyle = {
        fontWeight: 'bold',
        color: '#000',
    };

    // Responsive adjustments (optional)
    const responsiveStyles = window.innerWidth < 600 ? {
        containerStyle: {
            ...containerStyle,
            padding: '15px',
        },
        titleStyle: {
            ...titleStyle,
            fontSize: '20px',
        },
        contentStyle: {
            ...contentStyle,
            fontSize: '16px',
        },
    } : { containerStyle, titleStyle, contentStyle };

    return (
        <div style={responsiveStyles.containerStyle}>
            <div style={headerStyle}>
                <h2 style={responsiveStyles.titleStyle}>Checkout F</h2>
                <hr style={lineStyle} />
            </div>
            <div style={responsiveStyles.contentStyle}>
                <p style={itemStyle}>
                    <span style={boldTextStyle}>First Name:</span> {formData.firstName}
                </p>
                <p style={itemStyle}>
                    <span style={boldTextStyle}>Last Name:</span> {formData.lastName}
                </p>
                <p style={itemStyle}>
                    <span style={boldTextStyle}>Email:</span> {formData.email}
                </p>
                <p style={itemStyle}>
                    <span style={boldTextStyle}>Date:</span> {formData.date}
                </p>
                <p style={itemStyle}>
                    <span style={boldTextStyle}>Time:</span> {formData.time}
                </p>
            </div>
        </div>
    );
};

export default SummaryPage;
