import React from 'react';
import { useSelector } from 'react-redux';

const SummaryPage = () => {
    const formData = useSelector(state => state.formData);

    // Inline styles
    const containerStyle = {
        maxWidth: '600px',
        margin: '0 auto',
        padding: '20px',
        backgroundColor: '#f9f9f9',
        borderRadius: '8px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    };

    const titleStyle = {
        fontSize: '24px',
        marginBottom: '20px',
        textAlign: 'center',
        color: '#333',
    };

    const itemStyle = {
        fontSize: '18px',
        marginBottom: '10px',
        color: '#555',
    };

    const boldTextStyle = {
        fontWeight: 'bold',
        color: '#000',
    };

    // Responsive adjustments (optional)
    const responsiveStyles = window.innerWidth < 600 ? {
        containerStyle: {
            ...containerStyle,
            padding: '15px',
        },
        titleStyle: {
            ...titleStyle,
            fontSize: '20px',
        },
        itemStyle: {
            ...itemStyle,
            fontSize: '16px',
        },
    } : { containerStyle, titleStyle, itemStyle };

    return (
        <div style={responsiveStyles.containerStyle}>
            <h2 style={responsiveStyles.titleStyle}>Summary</h2>
            <p style={responsiveStyles.itemStyle}>
                <span style={boldTextStyle}>First Name:</span> {formData.firstName}
            </p>
            <p style={responsiveStyles.itemStyle}>
                <span style={boldTextStyle}>Last Name:</span> {formData.lastName}
            </p>
            <p style={responsiveStyles.itemStyle}>
                <span style={boldTextStyle}>Email:</span> {formData.email}
            </p>
            <p style={responsiveStyles.itemStyle}>
                <span style={boldTextStyle}>Date:</span> {formData.date}
            </p>
            <p style={responsiveStyles.itemStyle}>
                <span style={boldTextStyle}>Time:</span> {formData.time}
            </p>
        </div>
    );
};

export default SummaryPage;
